package org.calidadsoftware.questions;

import net.serenitybdd.screenplay.Actor;
import net.serenitybdd.screenplay.Question;
import net.serenitybdd.screenplay.targets.Target;

public class ActividadCompletada implements Question<Boolean> {

    private final Target target;

    public ActividadCompletada(Target target) {
        this.target = target;
    }

    public static ActividadCompletada mostrado() {
        return new ActividadCompletada(org.calidadsoftware.interfaces.ActivityPage.ACTIVITY_COMPLETED);
    }

    @Override
    public Boolean answeredBy(Actor actor) {
        try {
            return target.resolveFor(actor).isVisible();
        } catch (Exception e) {
            return false;
        }
    }
}
